SELECT sum(LO_EXTENDEDPRICE * LO_DISCOUNT) AS revenue FROM lineorder WHERE LO_ORDERDATE BETWEEN DATE '1993-01-01' AND DATE '1993-12-31' AND LO_DISCOUNT BETWEEN 1 AND 3 AND LO_QUANTITY < 25;
SELECT sum(LO_EXTENDEDPRICE * LO_DISCOUNT) AS revenue FROM lineorder WHERE LO_ORDERDATE >= DATE '1994-01-01' AND LO_ORDERDATE < DATE '1994-02-01' AND LO_DISCOUNT BETWEEN 4 AND 6 AND LO_QUANTITY BETWEEN 26 AND 35;
SELECT sum(LO_EXTENDEDPRICE * LO_DISCOUNT) AS revenue FROM lineorder WHERE LO_ORDERDATE BETWEEN DATE '1994-02-07' AND DATE '1994-02-13' AND LO_DISCOUNT BETWEEN 5 AND 7 AND LO_QUANTITY BETWEEN 26 AND 35;
SELECT sum(LO_REVENUE), EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, P_BRAND  FROM lineorder JOIN part ON LO_PARTKEY = P_PARTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE P_CATEGORY = 'MFGR#12' AND S_REGION = 'AMERICA' GROUP BY order_year, P_BRAND ORDER BY order_year, P_BRAND;
SELECT sum(LO_REVENUE), EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, P_BRAND  FROM lineorder JOIN part ON LO_PARTKEY = P_PARTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE P_BRAND >= 'MFGR#2221' AND P_BRAND <= 'MFGR#2228' AND S_REGION = 'ASIA' GROUP BY order_year, P_BRAND ORDER BY order_year, P_BRAND;
SELECT sum(LO_REVENUE), EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, P_BRAND  FROM lineorder JOIN part ON LO_PARTKEY = P_PARTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE P_BRAND = 'MFGR#2239' AND S_REGION = 'EUROPE' GROUP BY order_year, P_BRAND ORDER BY order_year, P_BRAND;
SELECT C_NATION, S_NATION, EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, sum(LO_REVENUE) AS revenue  FROM lineorder JOIN customer ON LO_CUSTKEY = C_CUSTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE C_REGION = 'ASIA' AND S_REGION = 'ASIA' AND LO_ORDERDATE >= DATE '1992-01-01' AND LO_ORDERDATE < DATE '1998-01-01' GROUP BY C_NATION, S_NATION, order_year ORDER BY order_year ASC, revenue;
SELECT C_CITY, S_CITY, EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, sum(LO_REVENUE) AS revenue  FROM lineorder JOIN customer ON LO_CUSTKEY = C_CUSTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE C_NATION = 'UNITED STATES' AND S_NATION = 'UNITED STATES' AND LO_ORDERDATE >= DATE '1992-01-01' AND LO_ORDERDATE < DATE '1998-01-01' GROUP BY C_CITY, S_CITY, order_year ORDER BY order_year ASC, revenue DESC;
SELECT C_CITY, S_CITY, EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, sum(LO_REVENUE) AS revenue  FROM lineorder JOIN customer ON LO_CUSTKEY = C_CUSTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE (C_CITY = 'UNITED KI1' OR C_CITY = 'UNITED KI5') AND (S_CITY = 'UNITED KI1' OR S_CITY = 'UNITED KI5') AND LO_ORDERDATE >= DATE '1992-01-01' AND LO_ORDERDATE < DATE '1998-01-01' GROUP BY C_CITY, S_CITY, order_year ORDER BY order_year ASC, revenue DESC;
SELECT C_CITY, S_CITY, EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, sum(LO_REVENUE) AS revenue  FROM lineorder JOIN customer ON LO_CUSTKEY = C_CUSTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY WHERE (C_CITY = 'UNITED KI1' OR C_CITY = 'UNITED KI5') AND (S_CITY = 'UNITED KI1' OR S_CITY = 'UNITED KI5') AND LO_ORDERDATE >= DATE '1997-12-01' AND LO_ORDERDATE <= DATE '1997-12-31' GROUP BY C_CITY, S_CITY, order_year ORDER BY order_year ASC, revenue DESC;
SELECT EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, C_NATION, sum(LO_REVENUE - LO_SUPPLYCOST) AS profit  FROM lineorder JOIN part ON LO_PARTKEY = P_PARTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY JOIN customer ON LO_CUSTKEY = C_CUSTKEY WHERE C_REGION = 'AMERICA' AND S_REGION = 'AMERICA' AND (P_MFGR = 'MFGR#1' OR P_MFGR = 'MFGR#2') GROUP BY order_year, C_NATION ORDER BY order_year ASC, C_NATION ASC;
SELECT EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, S_NATION, P_CATEGORY, sum(LO_REVENUE - LO_SUPPLYCOST) AS profit  FROM lineorder JOIN part ON LO_PARTKEY = P_PARTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY JOIN customer ON LO_CUSTKEY = C_CUSTKEY WHERE C_REGION = 'AMERICA' AND S_REGION = 'AMERICA' AND LO_ORDERDATE >= DATE '1997-01-01' AND LO_ORDERDATE < DATE '1999-01-01' AND (P_MFGR = 'MFGR#1' OR P_MFGR = 'MFGR#2') GROUP BY order_year, S_NATION, P_CATEGORY ORDER BY order_year ASC, S_NATION ASC, P_CATEGORY ASC;
SELECT EXTRACT(YEAR FROM LO_ORDERDATE) as order_year, S_CITY, P_BRAND, sum(LO_REVENUE - LO_SUPPLYCOST) AS profit  FROM lineorder JOIN part ON LO_PARTKEY = P_PARTKEY JOIN supplier ON LO_SUPPKEY = S_SUPPKEY JOIN customer ON LO_CUSTKEY = C_CUSTKEY WHERE S_NATION = 'UNITED STATES' AND LO_ORDERDATE >= DATE '1997-01-01' AND LO_ORDERDATE < DATE '1999-01-01' AND P_CATEGORY = 'MFGR#14' GROUP BY order_year, S_CITY, P_BRAND ORDER BY order_year ASC, S_CITY ASC, P_BRAND ASC;